(2, 'Olive Oil', 'Extra virgin olive oil', '<p>These extra virgin olive oils come solely from our grove in Umbria.  Tended in the traditional manner the olives are hand picked, cold  pressed and the resulting oil is unfiltered.</p>', 0, '', 0.00, 0.00, 0.00, 0.00, 5, 0, NULL, NULL, NULL, NULL, 2),
(3, 'Accessories', NULL, '', 0, '', 0.00, 0.00, 0.00, 0.00, 7, 0, NULL, NULL, NULL, NULL, 4),
(4, 'Gift Boxes', NULL, '', 0, '', 0.00, 0.00, 0.00, 0.00, 1, 0, NULL, NULL, NULL, NULL, 5),
(6, 'Adopt a tree', 'Adopt a tree', '<p style="text-align: center; "><span style="font-size: larger; "><strong><img width="383" height="272" align="middle" alt="" src="/images/uploads/treecut.jpg" /></strong></span></p>\r\n<p style="text-align: left; "><a href="/adopt-a-tree/adopt-a-tree/prod_21.html"><img width="432" height="36" alt="" src="/images/uploads/choosetree.jpg" /></a>&nbsp;</p>\r\n<p style="text-align: left; "><span style="font-size: larger; "><strong>A</strong></span><span style="font-size: larger; "><strong>dopt a  tree from Casa Margherita</strong> in the heart of <strong>Umbria, Italy </strong> and we will tend it for you during the following year  in the traditional manner. The <strong>olives from your tree</strong> will be picked by hand and watched over at the <strong>olive mill</strong> where they will be <em>cold pressed</em> until the </span><strong><a href="/olive-oil/cat_2.html"><span style="font-size: larger; ">olive oil</span></a></strong><span style="font-size: larger; "> comes pouring out golden green. As soon as its ready we send you your own </span><strong><a href="/olive-oil/cat_2.html"><span style="font-size: larger; ">extra virgin olive oil</span></a></strong><span style="font-size: larger; "> from your <strong>own tree</strong> so you can drizzle it on </span><strong><a href="http://www.casa-margherita.com/olive-oil/recipes/bruschetta.html"><span style="font-size: larger; ">bruschetta</span></a></strong><span style="font-size: larger; "> or whatever you fancy. During the year we will nuture <b>your tree</b> and update you on the work undertaken, the harvest prospects and weather generally.</span></p>\r\n<p style="text-align: left; "><span style="font-size: larger; "><strong>Adopting a tree</strong> as a <strong>gift</strong> couldn&rsquo;t be easier and the lucky recipient will be thanking you all year round.</span><b><br />\r\n</b></p>\r\n<p style="text-align: left; "><span style="font-size: larger; ">An adoption for one year costs &euro;71.50 (excluding shipping). For this you receive:-</span></p>\r\n<hr />\r\n<p><span style="font-size: larger; "><span style="color: rgb(128, 0, 0); ">On adoption: A beautiful box containing a 250ml tin of extra virgin olive oil, a certificate complete with personal gift message and an olive oil handbook.</span></span></p>\r\n<p><span style="font-size: larger; "><span style="color: rgb(128, 0, 128); ">In summer: &nbsp;Your own copy of the Casa Margherita cookbook packed with recipes to inspire you.</span></span></p>\r\n<p><span style="font-size: larger; "><span style="color: rgb(128, 128, 0); ">Autumn/Winter: A guaranteed 2 litres of extra virgin olive oil from your own tree.</span></span></p>\r\n<p><span style="color: rgb(0, 51, 0); "><span style="font-size: larger; ">We will also send you email updates of the progress of your tree and the work undertaken in the grove</span>.</span>&nbsp;</p>', 0, '', 0.00, 0.00, 0.00, 0.00, 1, 0, '', '', '', '', 1),
(7, 'Condiments & more', '', '', 0, '', 0.00, 0.00, 0.00, 0.00, 4, 0, NULL, NULL, NULL, NULL, 3);
